// Download word list as PDF (fixed!)
function downloadWordList() {
  if (currentWords.length === 0 || currentThemes.length === 0) {
    alert('No words selected to download. Please select at least one theme and click "Show Selected Themes".');
    return;
  }
  const pdfThemeTitle = document.getElementById('pdf-theme-title');
  const pdfTableBody = document.getElementById('pdf-word-table-body');
  pdfThemeTitle.textContent = `Themes: ${currentThemes.map(theme => theme.charAt(0).toUpperCase() + theme.slice(1).replace(/_/g, ' ')).join(', ')}`;
  pdfTableBody.innerHTML = '';
  currentWords.forEach(word => {
    const row = document.createElement('tr');
    row.innerHTML = `
      <td>${word.Article || '-'}</td>
      <td>${word.german}</td>
      <td>${word.english}</td>
      <td>${word.theme}</td>
    `;
    pdfTableBody.appendChild(row);
  });
  const element = document.getElementById('pdf-content');
  const opt = {
    margin: [10, 10, 10, 10],
    filename: `wordbank_${currentThemes.join('_')}.pdf`,
    image: { type: 'jpeg', quality: 0.98 },
    html2canvas: { scale: 2 },
    jsPDF: { unit: 'mm', format: 'a4', orientation: 'portrait' }
  };
  html2pdf().set(opt).from(element).save().catch(err => {
    alert('Failed to generate PDF. Please try again.');
  });
}

// UTILITY FUNCTIONS
function getRandomItems(arr, n) {
  const shuffled = [...arr].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, Math.min(n, arr.length));
}

function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

// CREATE AUDIO BUTTONS
function createAudioButton(word, language = 'de') {
  const cleanWord = word.replace(/[\s🔊]/g, '');
  return `<button class="audio-btn" onclick="playSingleAudio('${cleanWord.replace(/'/g,"\\'")}', ${language === 'de' ? 'true' : 'false'}, null)" aria-label="Play ${language === 'de' ? 'German' : 'English'} audio for ${cleanWord}">🔊</button>`;
}

// Play single audio
function playSingleAudio(word, isGerman, index) {
  stopAudio();
  if (index !== null) {
    const row = document.querySelector(`#word-table-body tr[data-index="${index}"]`);
    if (row) row.classList.add('playing');
  }
  const utterance = isGerman ? speakGerman(word, true) : speakEnglish(word);
  if (utterance) {
    utterance.onend = () => {
      if (index !== null) {
        const row = document.querySelector(`#word-table-body tr[data-index="${index}"]`);
        if (row) row.classList.remove('playing');
      }
    };
    utterance.onerror = () => {
      if (index !== null) {
        const row = document.querySelector(`#word-table-body tr[data-index="${index}"]`);
        if (row) row.classList.remove('playing');
      }
    };
  }
}

