// ====== MATCHING TEST (FULLY FIXED) ======
function startMatchingTest() {
  currentTest = 'matching';
  score = 0;
  selectedGerman = null;
  selectedEnglish = null;
  const selectedTheme = currentThemes[0] || Object.keys(wordbank)[0];
  questions = getRandomItems(wordbank[selectedTheme], Math.min(10, wordbank[selectedTheme].length));

  if (questions.length === 0) {
    renderToTestContent(`
      <div class="question">
        <h3>No words available in the selected theme.</h3>
        <p>Please select a different theme or add words to the wordbank.</p>
        <div class="controls">
          <button onclick="backToThemes('testbank')">🏠 Back to Themes</button>
        </div>
      </div>
    `);
    return;
  }

  // Create matched pairs by index (avoid duplicates and make matching reliable)
  let html = `
    <h2>🔗 Matching Test - Match German to English</h2>
    <div class="matching-container">
      <div class="column">
        <h3>German Words</h3>
  `;

  questions.forEach((item, idx) => {
    html += `
      <div class="match-item german" data-index="${idx}" data-german="${item.german}" data-english="${item.english}">
        ${item.german} ${createAudioButton(item.german, 'de')}
      </div>
    `;
  });

  html += `</div>
      <div class="column">
        <h3>English Words</h3>
  `;

  // Shuffle English column independently
  let englishShuffled = [...questions].sort(() => 0.5 - Math.random());
  englishShuffled.forEach((item, idx) => {
    html += `
      <div class="match-item english" data-index="${idx}" data-german="${item.german}" data-english="${item.english}">
        ${item.english} ${createAudioButton(item.english, 'en')}
      </div>
    `;
  });

  html += `
      </div>
    </div>
    <div id="score">Score: ${score}/${questions.length}</div>
    <div class="controls">
      <button onclick="startMatchingTest()" id="restart-btn" style="display:none;">🔄 Restart</button>
      <button onclick="backToThemes('testbank')">🏠 Home</button>
    </div>
  `;

  renderToTestContent(html);

  // Attach working handlers after rendering!
  setupMatchingHandlers();
}

function setupMatchingHandlers() {
  selectedGerman = null;
  selectedEnglish = null;
  let germanNodes = Array.from(document.querySelectorAll('.match-item.german'));
  let englishNodes = Array.from(document.querySelectorAll('.match-item.english'));

  germanNodes.forEach(gNode => {
    gNode.onclick = () => {
      if (gNode.classList.contains('matched-correct') || gNode.classList.contains('matched-incorrect')) return;
      germanNodes.forEach(el => el.classList.remove('selected'));
      englishNodes.forEach(el => el.classList.remove('selected'));
      gNode.classList.add('selected');
      selectedGerman = gNode;
      selectedEnglish = null;
      speakGerman(gNode.getAttribute('data-german'));
    };
  });

  englishNodes.forEach(eNode => {
    eNode.onclick = () => {
      if (eNode.classList.contains('matched-correct') || eNode.classList.contains('matched-incorrect')) return;
      englishNodes.forEach(el => el.classList.remove('selected'));
      eNode.classList.add('selected');
      selectedEnglish = eNode;

      if (!selectedGerman) {
        speakEnglish(eNode.getAttribute('data-english'));
        return;
      }

      // Check correct match!
      setTimeout(checkMatch, 100);
    };
  });
}

// Matching check logic (works by word pairs, not index)
function checkMatch() {
  if (!selectedGerman || !selectedEnglish) return;

  let germanWord = selectedGerman.getAttribute('data-german');
  let englishWord = selectedEnglish.getAttribute('data-english');

  if (
    germanWord === selectedEnglish.getAttribute('data-german') &&
    englishWord === selectedGerman.getAttribute('data-english')
    ) {
    selectedGerman.classList.add('matched-correct');
    selectedEnglish.classList.add('matched-correct');
    score++;
    speakGerman(germanWord);
    setTimeout(() => speakEnglish(englishWord), 500);
    document.getElementById('score').textContent = `Score: ${score}/${questions.length}`;
  } else {
    selectedGerman.classList.add('matched-incorrect');
    selectedEnglish.classList.add('matched-incorrect');
    setTimeout(() => {
      selectedGerman.classList.remove('matched-incorrect');
      selectedEnglish.classList.remove('matched-incorrect');
      document.querySelectorAll('.match-item').forEach(el => el.classList.remove('selected'));
      selectedGerman = null;
      selectedEnglish = null;
      speakEnglish(selectedGerman ? selectedGerman.getAttribute('data-english') : '');
    }, 1000);
  }

  // Show restart button if finished
  let remaining = document.querySelectorAll('.match-item.german:not(.matched-correct)').length;
  if (remaining === 0) {
    document.getElementById('restart-btn').style.display = 'inline-block';
  }
}

